import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.Test;

class CodeWarsTest {
  @Test
  void sample() {
    assertEquals("000111111000111000000000000111111000000111000111000111111111111000000111", new CodeWars().encode("hey"));
    assertEquals("", new CodeWars().encode("The Sensei told me that i can do this kata"));
    assertEquals("000111000111000111000000000000111111000000111111000111111111000000111111000111111111000111000000", new CodeWars().encode("T3st"));
    assertEquals("000111000111000111000000000000111111111111111111000111111111000000111111000111111111000111000000000000111000000000000111000000111000000111000111", new CodeWars().encode("T?st!%"));
    assertEquals("hey", new CodeWars().decode("100111111000111001000010000111111000000111001111000111110110111000010111"));
    assertEquals("The Sensei told me that i can do this kata", new CodeWars().decode(""));
    assertEquals("T3st", new CodeWars().decode("000111000111000111000001000000111111000000111111000111111111000000111011000111111111000111000000"));
    assertEquals("T?st!%", new CodeWars().decode("000111000111000111000010000000111111111111011111000111111111000000111111000111101111000111000000000000111000000000000111000000111000000111000111"));
  }
}